package Bpackage;

public class ForEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int array [] = {1,2,3,4,5};
		
		for(int i = 0 ; i <array.length ; i++) {
			System.out.println(i);
		}
		
		for(int i : array) {
			System.out.println(array[i]);
		}
		
		
		
	}

}
